import { PlayerType, EnhanceHTMLVideoElement, PlayerConfig } from '../types/Player'

/**
 * 播放器基类
 */
export class Player {
  /* 播放器配置 */
  public config: PlayerConfig
  /* 播放器容器 */
  public container: HTMLDivElement
  /* 播放器实例(Private) */
  public video: EnhanceHTMLVideoElement
  /* H265播放器画布 */
  public canvas: HTMLCanvasElement
  /* 播放器类型 */
  public codec: string
  /* 播放器类型 */
  public type: PlayerType
  /* 播放流地址 */
  public url: string
  /* 是否为直播模式 */
  public isLive: boolean
  /* 是否启用Debug模式 */
  public isDebug: boolean
  /* 是否启用自动播放 */
  public isAutoPlay: boolean
  /* 播放速率 */
  public playbackRate?: number
  /* 是否已暂停 */
  public isPaused: boolean
  /* 是否有音轨 */
  public hasAudio: boolean
  /* 音量值 */
  public volume: number
  /* 是否已禁音 */
  public isMuted: boolean
  /* 视频播放的当前位置(秒) */
  public currentTime: number
  /* 视频的长度(秒) */
  public duration: number
  /* 预加载视频长度 */
  public bufferedTime: number
  /* 是否加载中 */
  public isLoading: boolean
  /* 缩放比例 */
  public scale: string

  constructor(config: PlayerConfig) {
    this.config = config
    this.container = config.container
    this.type = config.type
    this.codec = config.codec
    this.url = config.url
    this.isLive = config.isLive
    this.isDebug = config.isDebug
    this.isAutoPlay = config.isAutoPlay
    this.playbackRate = config.playbackRate
    this.volume = config.volume
    this.isMuted = config.isMuted
    this.hasAudio = config.hasAudio
    this.isPaused = null
    this.currentTime = null
    this.duration = null
    this.bufferedTime = null
    this.isLoading = true
    this.scale = null
    this.init()
    this.bindEvent()
    this.setDefault()
  }

  /**
   * 开始播放
   */
  public play() {
    this.video.play()
  }

  /**
   * 暂停
   */
  public pause() {
    this.video.pause()
  }

  /**
   * 跳进(Seek)
   * @param time 秒
   */
  public seek(time: number) {
    this.video.currentTime = time
  }

  /**
   * 开关静音
   * @param isMuted 是否静音
   */
  public toggleMuteStatus(isMuted: boolean) {
    this.video.muted = isMuted
  }

  /**
   * 调整音量
   * @param volume 音量大小，取值0-1
   */
  public setVolume(volume: number) {
    this.video.volume = volume
  }

  /**
   * 设置播放速率
   * @param playbackRate 播放速率
   */
  public setPlaybackRate(playbackRate: number) {
    this.video.playbackRate = playbackRate
  }

  /**
   * 重新加载播放器
   */
  public reloadPlayer() {}

  /**
   * 销毁播放器
   */
  public disposePlayer() {
  }

  /**
   * 初始化
   */
  protected init() {}

  /**
   * 绑定事件
   */
  protected bindEvent() {}

  /**
   * 设置默认值
   */
  protected setDefault() {
    this.video.playbackRate = this.playbackRate
    this.video.volume = this.volume
    this.video.muted = this.isMuted
  }

  /**
   * 检测是否支持自动播放
   */
  protected testAutoPlay() {
    return new Promise(resolve => {
      const audio = document.createElement('audio')
      const mp4 = document.createElement('source')
      mp4.src = 'data:video/mp4;base64,'
      const webm = document.createElement('source')
      webm.src = 'data:video/webm;base64,'
      audio.appendChild(mp4)
      audio.appendChild(webm)
      document.body.appendChild(audio)

      const onLoad = (isSupport: boolean) => {
        audio.remove()
        resolve(isSupport)
      }

      audio.play().then(() => {
        onLoad(true)
      }).catch(e => {
        console.log(e)
        onLoad(false)
      })
    })
  }

  /**
   * 检测是否有音频
   * H265和RTC需要单独判断
   */
  protected testHasAudio() {
    // @ts-ignore
    this.hasAudio = this.video.mozHasAudio || Boolean(this.video.webkitAudioDecodedByteCount) || Boolean(this.video.audioTracks && this.video.audioTracks.length)
  }

  /**
   * 自动播放视频和音频
   * H265\HLS\RTC 拥有该方法， FLV 等没有
   */
  protected startAutoPlay() {
    if (this.isAutoPlay) {
      try {
        this.testAutoPlay().then(async isSupport => {
          if (isSupport) {
            await this.video.play()
          } else {
            this.video.muted = true
            await this.video.play()
          }
        })
      } catch (e) {
        this.isDebug && console.log(e)
      }
    }
  }

  /**
   * 回调事件
   * 当开始播放
   */
  protected onPlay() {
    this.config.onPlay && this.config.onPlay()
    this.isPaused = this.video.paused
  }

  /**
   * 回调事件
   * 当暂停
   */
  protected onPause() {
    this.config.onPause && this.config.onPause()
    this.isPaused = this.video.paused
  }

  /**
   * 回调事件
   * 当发起重试
   */
  protected onRetry(params?: any) {
    this.config.onRetry && this.config.onRetry(params)
  }

  /**
   * 回调事件
   * 当更新视频播放的当前位置
   */
  protected onTimeUpdate() {
    this.currentTime = this.video.currentTime
  }

  /**
   * 回调事件
   * 当更新视频的长度
   */
  protected onDurationChange() {
    this.isDebug && console.log('onDurationChange', this.video.duration)
    this.duration = this.video.duration
  }

  /**
   * 回调事件
   * 当调整音量
   */
  protected onVolumeChange() {
    this.volume = this.video.volume
    this.isMuted = this.video.muted
  }

  /**
   * 回调事件
   * 当调整播放速率
   */
  protected onRateChange() {
    this.playbackRate = this.video.playbackRate
  }

  /**
   * 回调事件
   * 当播放结束
   */
  protected onEnded() {
    this.config.onEnded && this.config.onEnded()
  }

  /**
   * 回调事件
   * 当跳跃视频时间
   */
  protected onSeeked() {
    this.currentTime = this.video.currentTime
    // this.config.onSeeked && this.config.onSeeked(this.video.currentTime)
  }

  /**
   * 回调事件
   * 当进行预加载
   */
  protected onBuffered() {
    if (this.video.buffered.length) {
      this.bufferedTime = this.video.buffered.end(this.video.buffered.length - 1)
      // this.config.onBuffered && this.config.onBuffered(this.video.buffered.end(this.video.buffered.length - 1))
    }
  }

  /**
   * 回调事件
   * 当开始加载
   */
  protected onLoadStart() {
    this.isLoading = true
    this.config.onLoadStart && this.config.onLoadStart()
  }

  /**
   * 回调事件
   * 当已加载完成可以播放
   */
  protected onCanplay() {
    this.isLoading = false
    this.testHasAudio()
    this.config.onCanplay && this.config.onCanplay()
  }
}
